	val memory = new Array[Byte](65536)
	var rotate: Byte = 0
	val outputAdt = false
	val outputDebug = true
	val verify = false
	val outputMemory = true
	val outputIo = true

	def assertOperation(op: Operation): Unit = {
		if (verify) {
			operations match {
				case head :: tail =>
					assert (head == op)
					operations = tail
				case _ =>
			}
		}
	}

	def readMemory(address: Int, code: Boolean): Int = {
		val r = memory(address).asInstanceOf[Int] & 0xFF
		if (outputAdt) print(f"MR(0x$address%04X,0x$r%02X,$code),")
		if (!outputAdt) assertOperation(MR(address, r))
		val isCode = if (code) " (code)" else ""

		if (outputMemory && outputDebug) println(f"    $r%02X <- mem[$address%04X]$isCode")
		r
	}

	def writeMemory(address: Int, data: Int): Unit = {
		if (outputMemory && outputDebug) println(f"    mem[$address%04X] <- $data%02X")

		if (outputAdt) print(f"MW(0x$address%04X, 0x$data%02X),")
		if (!outputAdt) assertOperation(MW(address, data))
		memory(address) = data.asInstanceOf[Byte]
	}

	def readIo(address: Int, code: Boolean): Int = {
		val b: Byte = address match {
			case 0x1235 => rotate
			case _ => 0
		}
		val r = b.asInstanceOf[Int] & 0xFF
		if (outputAdt) print(f"IOR(0x$address%04X, 0x$r%02X),")
		if (!outputAdt) assertOperation(IOR(address, r))
		if (outputIo && outputDebug) println(f"    $r%02X <- io[$address%04X]")
		r
	}

	def writeIo(address: Int, data: Int): Unit = {
		if (outputAdt) print(f"IOW(0x$address%04X, 0x$data%02X),")
		if (!outputAdt) assertOperation(IOW(address, data))
		if (outputIo && outputDebug) println(f"    io[$address%04X] <- $data%02X")
		address match {
			case 0x1235 =>
				rotate = ((data >>> 4) | (data << 4)).asInstanceOf[Byte]
			case _ =>
		}
	}

	def loadFileIntoMemory(filename: String, position: Int): Unit = {
		val file = new File(filename)
		using (new FileInputStream(file)) { reader => 
			val readBytes = reader.read(memory, position, file.length().asInstanceOf[Int])
			assert (readBytes == file.length)
		}
	}

	def main(args: Array[String]) {
		loadFileIntoMemory("rc800/spinal/code.bin", 0)
		
		SimConfig
		.withWave
		.compile(new RC811(rc800.lpm.generic.Components))
		.doSim(1673871647) { dut =>
			// Fork a process to generate the reset and the clock on the dut
			dut.clockDomain.forkStimulus(period = 10)

			dut.clockDomain.waitSampling()

			dut.io.irq #= false

			var modelState = 0
			for (idx <- 0 to 600) {
				dut.clockDomain.waitFallingEdge()

				/*
				if (idx == 10)
					dut.io.irq #= true
					*/

				val pc = dut.pc.toInt
				val stage = dut.stage.toInt

				/*
				if (stage == 0)
					println(f"PC: $pc%04X/$stage")
				else
					println(s"    ----/$stage")
				*/
					
				if (dut.io.busEnable.toBoolean) {
					if (dut.io.write.toBoolean) {
						val writer: (Int, Int) => Unit = if (dut.io.io.toBoolean) writeIo else writeMemory
						writer(dut.io.address.toInt, dut.io.dataOut.toInt)
					} else {
						val reader: (Int, Boolean) => Int = if (dut.io.io.toBoolean) readIo else readMemory
						dut.io.dataIn #= reader(dut.io.address.toInt, dut.io.code.toBoolean) & 0xFF
					}
				}
			}
		}
	}
